import { environment } from '@environment/environment';
export function appInitializer(authService) {
  return () => new Promise(resolve => {
    // wait for facebook sdk to initialize before starting the angular app
    window['fbAsyncInit'] = function () {
      FB.init({
        appId: environment.facebookAppId,
        cookie: true,
        xfbml: true,
        version: 'v8.0'
      }); // auto authenticate with the api if already logged in with facebook

      FB.getLoginStatus(({
        authResponse
      }) => {
        if (authResponse) {
          console.log(authResponse);
        } else {}

        resolve("");
      });
    }; // load facebook sdk script


    (function (d, s, id) {
      var js,
          fjs = d.getElementsByTagName(s)[0];

      if (d.getElementById(id)) {
        return;
      }

      js = d.createElement(s);
      js.id = id;
      js.src = 'https://connect.facebook.net/en_US/sdk.js';
      fjs.parentNode.insertBefore(js, fjs);
    })(document, 'script', 'facebook-jssdk');
  }); //get access token from localStorage

  let accessToken = authService.getAccesTokenFromLocalStorage(); //if not null get refresh token and get user infor

  return () => new Promise(resolve => {
    if (accessToken) {
      //get new token
      authService.refreshToken().subscribe(res => {
        //get user information
        authService.userInfor().subscribe();
      }).add(resolve);
    }

    resolve();
  });
}
